import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router";
import DOMPurify from 'dompurify';

export default function NewTransaction() {
    const [form, setForm] = useState({
        accountnum: "",
        transactionAmount: "",
        transactionAddress: "",
        transactionStatus: "Pending",
        currency: "USD", // Default currency
    });
    const navigate = useNavigate();
    const token = localStorage.getItem("jwt");

    useEffect(() => {
        const accountnum = localStorage.getItem("accountnum");
        console.log(accountnum);
        if (accountnum) {
            setForm((prev) => ({ ...prev, accountnum: accountnum }));
        }
    }, []);

    function updateForm(value) {
        setForm((prev) => ({ ...prev, ...value }));
    }

    async function onSubmit(e) {
        e.preventDefault();
        console.log("Form state on submit:", form);

        // Sanitize form data
        const sanitizedForm = {
            accountnum: DOMPurify.sanitize(form.accountnum),
            transactionAmount: DOMPurify.sanitize(form.transactionAmount),
            transactionAddress: DOMPurify.sanitize(form.transactionAddress),
            transactionStatus: DOMPurify.sanitize(form.transactionStatus),
            currency: DOMPurify.sanitize(form.currency), // Sanitized currency field
        };

        try {
            const response = await fetch("https://localhost:3001/transaction/newtransaction", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer ${token}`,
                },
                body: JSON.stringify(sanitizedForm),
            });

            if (!response.ok) {
                throw new Error("Transaction failed");
            }

            const data = await response.json();
            const { token: newToken, accountnum } = data;
            console.log(`Account Num: ${accountnum}\nToken: ${newToken}`);

            setForm({ accountnum: accountnum, transactionAmount: "", transactionAddress: "", currency: "USD" }); // Reset currency to default
            navigate("/viewtransactions");
        } catch (error) {
            window.alert(error);
        }
    }

    return (
        <div className="App-header">
            <div className="transaction-container">
                <h2 className="App"> New Transaction</h2>
                <form onSubmit={onSubmit}>
                    <table className="table table-striped" style={{ marginTop: 20 }}>
                        <thead>
                            <tr>
                                <th>Account Number</th>
                                <th>Transaction Amount</th>
                                <th>Transaction Address</th>
                                <th>Currency</th>
                                <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                <td><input type="text" value={form.accountnum} readOnly /></td>
                                <td><input type="number" value={form.transactionAmount} onChange={(e) => updateForm({ transactionAmount: e.target.value })} placeholder="Enter amount" /></td>
                                <td><input type="text" value={form.transactionAddress} onChange={(e) => updateForm({ transactionAddress: e.target.value })} placeholder="Enter SWIFT Code" /></td>
                                <td>
                                    <select 
                                        value={form.currency} 
                                        onChange={(e) => updateForm({ currency: e.target.value })}
                                    >
                                        <option value="USD">USD</option>
                                        <option value="EUR">EUR</option>
                                        <option value="GBP">ZAR</option>
                                        <option value="INR">INR</option>
                                        <option value="AUD">AUD</option>
                                    </select>
                                </td>
                                <td><button type="submit">Pay Now</button></td>
                            </tr>
                        </tbody>
                    </table>
                </form>
            </div>
        </div>
    );
}
