import React, { useState, useEffect } from "react";

export default function Transactions() {
    const [transactions, setTransactions] = useState([]);
    const token = localStorage.getItem("jwt");
    const accountnum = localStorage.getItem("accountnum");

    useEffect(() => {
        async function fetchTransactions() {
            try {
                const response = await fetch(`https://localhost:3001/transaction/alltransactions`, {
                    method: "GET",
                    headers: {
                        "Content-Type": "application/json",
                        "Authorization": `Bearer ${token}`
                    }
                });
                if (!response.ok) {
                    throw new Error("Failed to fetch transactions");
                }
                const data = await response.json();
                setTransactions(data);
            } catch (error) {
                console.error("Error:", error);
            }
        }
        fetchTransactions();
    }, [accountnum, token]);

    return (
        <div className="App-header" style={{
            backgroundImage: `url("https://media.istockphoto.com/id/1432744240/vector/concept-of-banking-and-finance.jpg?s=612x612&w=0&k=20&c=5YCtZpM5o1_MUnYqKvvCYA4ceby3JcU6faq7nUafhRI=")`,
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            padding: '20px'
        }}>
            <div className="transaction-container">
                <h2 className="App">Transactions</h2>
                <table className="table table-striped" style={{ marginTop: 20 }}>
                    <thead>
                        <tr>
                            <th>User</th>
                            <th>Transaction Amount</th>
                            <th>Transaction Address</th>
                            <th>Status</th>
                        </tr>
                    </thead>
                    <tbody>
                        {transactions.map((transaction, index) => (
                            <tr key={index}>
                                <td>{transaction.accountnum}</td>
                                <td>{transaction.transactionAmount}</td>
                                <td>{transaction.transactionAddress}</td>
                                <td>{transaction.transactionStatus}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}
