import React, { useState } from "react";
import { useNavigate } from "react-router";
import { useAuth } from "./AuthProvider";
import "./login.css"; // Import your custom CSS file

export default function Login() {
    const [form, setForm] = useState({
      username: "",
      accountnum: "",
      password: "",
    });
    const { login } = useAuth();
    const navigate = useNavigate();
  
    function updateForm(value) {
      setForm((prev) => ({ ...prev, ...value }));
    }
  
    async function onSubmit(e) {
      e.preventDefault();
  
      try {
        const response = await fetch("https://localhost:3001/user/login", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(form),
        });
  
        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(errorData.message || "Login failed");
        }
  
        const data = await response.json();
        const { token, accountnum } = data;
  
        login(token, accountnum);
  
        setForm({ username: "", accountnum: "", password: "" });
        navigate("/newtransaction");
      } catch (error) {
        window.alert(error);
      }
    }

    return (
        <div className="App-header">
            <div className="login-container">
                <h2>Login to Your Account</h2>
                <form onSubmit={onSubmit}>
                    <div className="form-group">
                        <label htmlFor="username">Username</label>
                        <input
                            type="text"
                            className="form-control"
                            id="username"
                            value={form.username}
                            onChange={(e) => updateForm({ username: e.target.value })}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="accountnum">Account Number</label>
                        <input
                            type="text"
                            className="form-control"
                            id="accountnum"
                            value={form.accountnum}
                            onChange={(e) => updateForm({ accountnum: e.target.value })}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Password</label>
                        <input
                            type="password"
                            className="form-control"
                            id="password"
                            value={form.password}
                            onChange={(e) => updateForm({ password: e.target.value })}
                        />
                    </div>
                    <div className="form-group">
                        <input
                            type="submit"
                            value="Login"
                            className="btn btn-primary"
                        />
                    </div>
                </form>
                <a href="#" className="footer-link">Forgot Password?</a>
            </div>
        </div>
    );
}
